mapper.select:
SELECT
    COUNT(SR_NO)
FROM SR sr, SYSTEM system, SR_STTS stts, USERS users, SR_TYPE type
where sr.sys_no = system.sys_no
AND sr.stts_no = stts.stts_no
AND sr.sr_cust_no = users.user_no
AND sr.sr_type = type.sr_type_no

	AND sr.sys_no = #{sysNo}


	AND sr.sr_type = #{srTypeNo}


	AND sr.stts_no = #{srSttsNo}


	AND sr.sr_ttl LIKE #{srName}


	AND sr.sr_no LIKE #{srNo}

SELECT rownum, SR_NO AS "srNo", SYS_NM AS "sysNm", SR_TYPE_NM AS "srTypeNm", SR_TTL AS "srTtl", USER_NM AS "userNm", SR_DDLN_DATE AS "srDdlnDate", STTS_NM AS "sttsNm", SR_PRY AS "srPry" FROM( SELECT rownum as rnum, SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR_PRY FROM( SELECT SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR_PRY FROM( SELECT SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR_PRY FROM SR sr, SYSTEM system, SR_STTS stts, USERS users, SR_TYPE type where sr.sys_no = system.sys_no AND sr.stts_no = stts.stts_no AND sr.sr_cust_no = users.user_no AND sr.sr_type = type.sr_type_no
AND sr.sys_no = #{sysNo}
AND sr.sr_type = #{srTypeNo}
AND sr.stts_no = #{srSttsNo}
AND sr.sr_ttl LIKE #{srName}
AND sr.sr_no LIKE #{srNo}
) ORDER BY SR_PRY )
WHERE rownum <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
UPDATE HR
SET 
    HR_START_DATE = #{hrStartDate},
    HR_END_DATE = #{hrEndDate}
WHERE SR_NO = #{srNo}
AND USER_NO = #{userNo}
