mapper.select:

		select rnum, qstn_no, qstn_ttl, qstn_cn, qstn_inq_cnt, qstn_wrt_date, user_id
		from(
	   		select rownum as rnum, qstn_no, qstn_ttl, qstn_cn, qstn_inq_cnt, qstn_wrt_date, user_id
	   		from(
	     		select qstn_no, qstn_ttl, qstn_cn, qstn_inq_cnt, qstn_wrt_date, user_id
	      		from qstn
	      		order by qstn_no desc
	    	) 
   			 where rownum <= {endRowNo}
		)
		where rnum >= #{startRowNo};
