mapper.select:
SELECT
    COUNT(SR_NO)
FROM SR sr, SYSTEM system, SR_STTS stts, USERS users
where sr.sys_no = system.sys_no
AND sr.stts_no = stts.stts_no
AND sr.sr_cust_no = users.user_no

	AND sr.sr_dev_dp LIKE #{srDevDp}


	AND sr.sys_no = #{sysNo}


	AND sr.stts_no = #{sttsNo}

SELECT SR_NO AS "srNo", SR_TTL AS "srTtl", SYS_NM "sysNm", USER_NM AS "userNm", USER_DP_NM AS "userDpNm", USER_OGDP AS "userOgdp", SR_DEV_DP AS "srDevDp", STTS_NM AS "sttsNm", SR_REG_DATE AS "srRegDate", SR_DDLN_DATE AS "srDdlnDate", SR_PRY AS "srPry", RNUM AS "seq" FROM( SELECT SR_NO, SR_TTL, SYS_NM, USER_NM, USER_DP_NM, USER_OGDP, SR_DEV_DP, STTS_NM, SR_REG_DATE, SR_DDLN_DATE, SR_PRY, ROWNUM AS RNUM FROM( SELECT s.SR_NO, s.SR_TTL, SYS_NM, u.USER_NM, u.USER_DP_NM, u.USER_OGDP, s.SR_DEV_DP, st.STTS_NM, s.SR_REG_DATE, s.SR_DDLN_DATE, s.SR_PRY FROM SR s ,USERS u, SR_STTS st, SYSTEM sys WHERE SR_CUST_NO = u.USER_NO AND s.STTS_NO = st.STTS_NO AND s.SYS_NO = sys.SYS_NO
AND st.stts_no = #{sttsNo}
AND sys.sys_nm = #{sysNo}
AND u.user_dp_nm LIKE #{srDevDp}
ORDER BY s.SR_NO )
WHERE ROWNUM <=#{end}
)
WHERE RNUM >=#{start}
