mapper.select:
SELECT COUNT(*) 
	FROM SR SR, SYSTEM SYS, SR_STTS ST, USERS U 
WHERE SR.SYS_NO = SYS.SYS_NO
AND SR.STTS_NO = ST.STTS_NO
AND SR.SR_CUST_NO = U.USER_NO
AND SR.STTS_NO IN (4, 9)
AND sr.SR_REG_DATE BETWEEN TO_DATE(#{srRegStartDate},'YY-MM-DD')  AND TO_DATE(#{srRegEndDate},'YY-MM-DD')+1

	AND sys.sys_no = #{sysNo}


	AND st.stts_no = #{sttsNo}


	AND u.user_ogdp = #{userOgdp}


	AND	SR.SR_DEV_DP = #{srDevDp}


	AND  sr.SR_TTL like #{srTtl}


	AND U.USER_TYPE = #{userType}
	AND sr_Cust_No = #{srCustNo}

select rnum AS "seq", sr_no AS "srNo", sr_cust_no AS "srCustId", sys_no AS "sysNo", sys_nm as "sysNm", stts_no AS "sttsNo", stts_nm AS "sttsNm", sr_ttl AS "srTtl", sr_cn AS "srCn", sr_reg_date AS "srRegDate", sr_pry AS "srPry", sr_opnn AS "srOppn", sr_bgt AS "srBgt", sr_dev_cn AS "srDevCn", sr_ddln_date AS "srDdlnDate", sr_start_date AS "srStartDate", sr_end_date AS "srEndDate", sr_del_yn AS "srDelYn", sr_type AS "srType", sr_dev_dp as "srDevDp", user_type AS "userType", user_nm AS "userNm", user_ogdp AS "userOgdp", user_dp_nm AS "userDpNm" FROM( SELECT ROWNUM AS RNUM, SR_NO, SR_CUST_NO, SYS_NO,SYS_NM, STTS_NO, STTS_NM, SR_TTL, SR_CN, SR_REG_DATE, SR_PRY, SR_OPNN, SR_BGT, SR_DEV_CN, SR_DDLN_DATE, SR_START_DATE, SR_END_DATE, SR_DEL_YN, SR_TYPE, SR_DEV_DP, USER_TYPE, USER_NM, USER_OGDP, USER_DP_NM FROM( SELECT SR_NO, SR_CUST_NO, SR.SYS_NO, SYSTEM.SYS_NM, SR.STTS_NO, SR_STTS.STTS_NM, SR_TTL, SR_CN, SR_REG_DATE, SR_PRY, SR_OPNN, SR_BGT, SR_DEV_CN, SR_DDLN_DATE, SR_START_DATE, SR_END_DATE, SR_DEL_YN, SR.SR_TYPE, SR.SR_DEV_DP, USER_TYPE, USER_NM, USER_OGDP, USER_DP_NM FROM SR, SR_TYPE, SR_STTS, USERS, SYSTEM WHERE SR.STTS_NO = SR_STTS.STTS_NO AND SYSTEM.SYS_NO = SR.SYS_NO AND SR.SR_CUST_NO = USERS.USER_NO AND SR.SR_TYPE = SR_TYPE.SR_TYPE_NO AND SR.STTS_NO IN (4, 9) AND sr.SR_REG_DATE BETWEEN TO_DATE(#{srRegStartDate},'YY-MM-dd') AND TO_DATE(#{srRegEndDate},'YY-MM-dd')+1
AND SR.STTS_NO = #{sttsNo}
AND SR.SYS_NO = #{sysNo}
AND user_ogdp = #{userOgdp}
AND SR.SR_DEV_DP = #{srDevDp}
AND sr.SR_TTL like #{srTtl}
AND sr_cust_no= #{srCustNo} AND USERS.USER_TYPE = #{userType}
ORDER BY SR_STTS.STTS_NO, SR.SR_PRY, SR.SR_REG_DATE DESC ) aa
where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}

SELECT USERS.USER_NO AS "userNo", USERS.USER_NM AS "userNm", USERS.USER_DP_NM AS "userDpNm", USERS.USER_JBPS AS "userJbps"
FROM USERS, HR
WHERE USERS.USER_NO = HR.USER_NO AND HR_LEADER = 'Y' AND SR_NO=#{srNo}

mapper.update:
		UPDATE SR SET SR_DEV_CN=#{srDevCn}, SR_BGT=#{srBgt}, STTS_NO = #{sttsNo}, SR_DDLN_DATE=#{srDdlnDate}, SR_START_DATE=#{srStartDate},
    			SR_END_DATE=#{srEndDate}, SR_DEV_DP=#{srDevDp}
		WHERE SR_NO=#{srNo}

mapper.select:
SELECT USER_NM AS "userNm", USER_NO AS "userNo", SYS_NO AS "sysNo", SYS_NM AS "sysNm"
FROM USERS, SYSTEM
WHERE USERS.USER_NO = SYSTEM.MANAGER_NO AND USER_NO = #{userNo}
