mapper.select:
SELECT COUNT(SR_NO) FROM SR sr, SYSTEM sys, SR_STTS st, USERS u WHERE sr.sys_no = sys.sys_no AND sr.stts_no = st.stts_no AND sr.sr_cust_no = u.user_no AND sr.stts_no !=3 AND sr.stts_no !=4 AND sr.stts_no !=5 AND sr.stts_no !=6
AND sys.sys_no = #{sysNo}
AND st.stts_no = #{sttsNo}
AND u.user_ogdp = #{userOgdp}
AND u.user_dp_nm = #{userDpNm}
AND sr.SR_REG_DATE BETWEEN TO_DATE(#{srRegStartDate},'YY-MM-DD') AND TO_DATE(#{srRegEndDate},'YY-MM-DD')+0.9
AND sr.SR_TTL like #{srTtl}

SELECT SR_NO AS "srNo", SR_TTL AS "srTtl", SR_REG_DATE AS "srRegDate", SR_DDLN_DATE AS "srDdlnDate", SR_PRY AS "srPry", USER_NM AS "userNm", USER_OGDP AS "userOgdp", USER_DP_NM AS "userDpNm", STTS_NM AS "sttsNm", SYS_NM "sysNm", RNUM AS "seq" FROM( SELECT SR_NO, SR_TTL, SR_REG_DATE, SR_DDLN_DATE, SR_PRY, USER_NM, USER_OGDP, USER_DP_NM, STTS_NM, SYS_NM, ROWNUM AS RNUM FROM( SELECT s.SR_NO, s.SR_TTL, s.SR_REG_DATE, s.SR_DDLN_DATE, s.SR_PRY, u.USER_NM, u.USER_OGDP, u.USER_DP_NM, st.STTS_NM, SYS_NM FROM SR s ,USERS u, SR_STTS st, SYSTEM sys WHERE SR_CUST_NO = u.USER_NO AND s.STTS_NO = st.STTS_NO AND s.SYS_NO = sys.SYS_NO AND s.stts_no !=3 AND s.stts_no !=4 AND s.stts_no !=5 AND s.stts_no !=6
AND st.stts_no = #{sttsNo}
AND sys.sys_nm = #{sysNo}
AND u.user_ogdp = #{userOgdp}
AND u.user_dp_nm = #{userDpNm}
AND s.SR_REG_DATE BETWEEN TO_DATE(#{srRegStartDate},'YY-MM-DD') AND TO_DATE(#{srRegEndDate},'YY-MM-DD')+0.9
AND s.SR_TTL like #{srTtl}
ORDER BY s.SR_NO )
WHERE ROWNUM <=#{endRowNo}
)
WHERE RNUM >=#{startRowNo}

mapper.update:
UPDATE SR
SET STTS_NO = (
    SELECT STTS_NO
    FROM SR_STTS
    WHERE STTS_NM = #{sttsNm}
),
SR_PRY = #{srPry},
SR_OPNN = #{srOpnn},
SR_TYPE = (
    SELECT SR_TYPE_NO
    FROM SR_TYPE
    WHERE SR_TYPE_NM = #{srReqSe}
)
WHERE SR_NO = #{srNo}

UPDATE SR
SET STTS_NO = (
    SELECT STTS_NO
    FROM SR_STTS
    WHERE STTS_NM = #{sttsNm}
),
SR_PRY = #{srPry},
SR_OPNN = #{srOpnn},
SR_TYPE = (
    SELECT SR_TYPE_NO
    FROM SR_TYPE
    WHERE SR_TYPE_NM = #{srReqSe}
)
WHERE SR_NO = #{srNo}
