mapper.select:
    SELECT
	    COUNT(sr.SR_NO)
	FROM SR sr, SYSTEM system, SR_STTS stts

    , HR hr

	WHERE sr.sys_no = system.sys_no
	AND stts.stts_no = sr.stts_no
	AND TO_CHAR(SR_REG_DATE, 'YYYY') = TO_CHAR(sysdate, 'YYYY')

    	AND SR_CUST_NO = #{srCustNo}
    	AND sr.sys_no = #{sysNo}


   		AND sr.sys_no = #{sysNo}


    	AND SR.STTS_NO = #{sttsNo}


		AND hr.sr_no = sr.sr_no
		AND user_no = #{srCustNo}

SELECT rownum, SR_NO AS "srNo", SR_TTL AS "srTtl", SYS_NM AS "sysNm", stts_NM AS "sttsNm", SR_REG_DATE AS "srRegDate", SR_PRY AS "srPry" FROM ( SELECT rownum as rnum, SR_NO, SR_TTL, SYS_NM, STTS_NM, SR_REG_DATE, SR_PRY FROM ( SELECT SR_NO, SR_TTL, SYS_NM, STTS_NM, SR_REG_DATE, SR_PRY FROM ( SELECT sr.SR_NO AS "SR_NO", SR_TTL, SYS_NM, stts_NM, SR_REG_DATE, SR_PRY FROM SR sr, SYSTEM system, SR_STTS stts
, HR hr
WHERE sr.sys_no = system.sys_no AND stts.stts_no = sr.stts_no AND TO_CHAR(SR_REG_DATE, 'YYYY') = TO_CHAR(sysdate, 'YYYY')
AND SR_CUST_NO = #{srCustNo} AND sr.sys_no = #{sysNo}
AND sr.sys_no = #{sysNo}
AND SR.STTS_NO = #{sttsNo}
AND hr.sr_no = sr.sr_no AND user_no = #{srCustNo}
) ORDER BY SR_PRY, SR_REG_DATE DESC, SR_NO )
WHERE rownum <= #{end}
)
WHERE rnum >= #{start}
