mapper.update:
UPDATE NOTICE
SET NTC_INQ_CNT = NTC_INQ_CNT+1
WHERE NTC_NO = #{ntcNo}

UPDATE NOTICE
SET NTC_CN = #{ntcCn}
WHERE NTC_NO = #{ntcNo}

UPDATE NTC_FILE
SET 
	NTC_FILE_ACTL_NM = #{ntcFileActlNm},
	NTC_FILE_PHYS_NM = #{ntcFilePhysNm},
	NTC_FILE_EXTN_NM = #{ntcFileExtnNm}
WHERE NTC_NO = #{ntcNo}

UPDATE NTC_COMMENT 
SET NTC_CMNT_CN = #{ntcCmntCn},
    NTC_CMNT_DATE = sysdate
WHERE NTC_NO = #{ntcNo}
AND NTC_CMNT_NO = #{ntcCmntNo}
