mapper.select:
SELECT COUNT(SR_NO)
FROM (
	SELECT
	    SR.SR_NO
	FROM SR sr, SYSTEM system, SR_STTS stts, USERS users, SR_TYPE type, HR hr
	where sr.sys_no = system.sys_no
	AND sr.stts_no = stts.stts_no
	AND sr.sr_cust_no = users.user_no
	AND sr.sr_type = type.sr_type_no
	AND sr.sr_no = hr.sr_no

		AND sr.sys_no = #{sysNo}


		AND sr.sr_type = #{srTypeNo}


		AND sr.stts_no = #{srSttsNo}


		AND sr.sr_ttl LIKE #{srName}


		AND sr.sr_no LIKE #{srNo}


		AND hr.user_no = #{userNo}


		AND sr.sys_no = #{adminSysNo}

	AND (sr.stts_no = 5 or sr.stts_no = 6 or sr.stts_no = 8 or sr.stts_no = 10)
	GROUP BY SR.SR_NO
)

SELECT rownum, SR_NO AS "srNo", SYS_NM AS "sysNm", SR_TYPE_NM AS "srTypeNm", SR_TTL AS "srTtl", USER_NM AS "userNm", SR_DDLN_DATE AS "srDdlnDate", STTS_NM AS "sttsNm", SR_PRY AS "srPry" FROM( SELECT rownum as rnum, SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR_PRY FROM( SELECT SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR_PRY FROM( SELECT SR.SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR.STTS_NO, SR_PRY FROM SR sr, SYSTEM system, SR_STTS stts, USERS users, SR_TYPE type, HR hr where sr.sys_no = system.sys_no AND sr.stts_no = stts.stts_no AND sr.sr_cust_no = users.user_no AND sr.sr_type = type.sr_type_no AND sr.sr_no = hr.sr_no
AND sr.sys_no = #{sysNo}
AND sr.sr_type = #{srTypeNo}
AND sr.stts_no = #{srSttsNo}
AND sr.sr_ttl LIKE #{srName}
AND sr.sr_no LIKE #{srNo}
AND hr.user_no = #{userNo}
AND sr.sys_no = #{adminSysNo}
AND (sr.stts_no = 5 or sr.stts_no = 6 or sr.stts_no = 8 or sr.stts_no = 10) GROUP BY SR.SR_NO, SYS_NM, SR_TYPE_NM, SR_TTL, USER_NM, SR_DDLN_DATE, STTS_NM, SR.STTS_NO, SR_PRY ) ORDER BY DECODE(STTS_NO, 8, 1, 10, 2, 5, 3) ASC, SR_PRY, SR_DDLN_DATE DESC )
WHERE rownum <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
UPDATE HR
SET 
    HR_START_DATE = #{hrStartDate},
    HR_END_DATE = #{hrEndDate}
WHERE SR_NO = #{srNo}
AND USER_NO = #{userNo}

mapper.delete:
DELETE HR
WHERE SR_NO = #{srNo}
AND USER_NO = #{userNo}

mapper.update:
UPDATE PROGRESS
SET
    PROG_START_DATE = #{progStartDate, jdbcType=VARCHAR},
    PROG_END_DATE = #{progEndDate, jdbcType=VARCHAR},
    PROG_RATE = #{progRate}
WHERE PROG_NO = #{progNo}

mapper.select:
SELECT
    PROG_NO AS "progNo"
FROM PROGRESS
WHERE SR_NO = #{srNo}
AND PROG_TYPE = #{progTypeNo}

mapper.update:
UPDATE SR SET STTS_NO = 5
, SR_DDLN_DATE = #{date}, SR_END_DATE = #{crDdlnDate}
WHERE SR_NO = #{srNo}

UPDATE CHANGE_REQUEST
SET

    CR_RESULT = '승인'


    CR_RESULT = '반려',
    CR_COMMENT = #{crComment}

WHERE CR_NO = #{crNo}

UPDATE HR
SET 
    HR_END_DATE = #{hrEndDate}
WHERE SR_NO = #{srNo}
AND USER_NO = #{userNo}
