mapper.select:
SELECT rnum AS "rnum", QSTN_NO AS "qstnNo", QSTN_TTL AS "qstnTtl", QSTN_INQ_CNT AS "qstnInqCnt", QSTN_WRT_DATE AS "qstnWrtDate", USER_NO AS "userNo", USER_NM AS "userNm" FROM( SELECT ROWNUM AS RNUM, QSTN_NO, QSTN_TTL, QSTN_INQ_CNT, QSTN_WRT_DATE, USER_NO, USER_NM FROM( SELECT QSTN_NO, QSTN_TTL, QSTN_INQ_CNT, QSTN_WRT_DATE, QSTN.USER_NO, USER_NM FROM QSTN, USERS WHERE QSTN.USER_NO = USERS.USER_NO AND SYS_NO = #{sysNo} AND QSTN_WRT_DATE BETWEEN TO_DATE(#{startDate},'YY-MM-DD') AND TO_DATE(#{endDate},'YY-MM-DD')+0.9
AND QSTN_TTL like #{qstnTtl}
ORDER BY QSTN_NO DESC )
where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}

		SELECT COUNT(*) FROM QSTN 	
		WHERE SYS_NO=#{sysNo}

		 AND QSTN_WRT_DATE BETWEEN 
                TO_DATE(#{startDate},'YY-MM-DD') 
                AND TO_DATE(#{endDate},'YY-MM-DD')+0.9


			AND QSTN_TTL like #{qstnTtl}
			AND SYS_NO = #{sysNo}

mapper.update:
UPDATE QSTN
SET QSTN_INQ_CNT = QSTN_INQ_CNT+1
WHERE QSTN_NO = #{qstnNo}
