mapper.select:

		SELECT
		    COUNT(sr.SR_NO)

			FROM SR sr, SYSTEM system, SR_STTS stts, USERS users

			    , HR hr

			WHERE sr.SR_CUST_NO = users.USER_NO
			AND sr.sys_no = system.sys_no
			AND sr.stts_no = stts.stts_no
			AND sr.SR_DEL_YN = 'N'

			AND sr.sr_dev_dp = #{srDevDp}


			AND sr.sys_no = #{sysNo}


			AND sr.stts_no = #{sttsNo}


			AND users.user_ogdp = #{userOgdp}


			 AND sr.SR_REG_DATE BETWEEN 
                TO_DATE(#{srRegStartDate},'YY-MM-DD') 
                AND TO_DATE(#{srRegEndDate},'YY-MM-DD')+0.9


			AND  sr.SR_TTL like #{srTtl}


			AND  sr.SR_CUST_NO = #{userNo}


			AND  system.MANAGER_NO = #{userNo}


			AND hr.sr_no = sr.sr_no
			AND hr.user_no = #{userNo}

SELECT SR_NO AS "srNo", SR_TTL AS "srTtl", SYS_NM "sysNm", USER_NM AS "userNm", USER_DP_NM AS "userDpNm", USER_OGDP AS "userOgdp", SR_DEV_DP AS "srDevDp", STTS_NM AS "sttsNm", SR_REG_DATE AS "srRegDate", SR_DDLN_DATE AS "srDdlnDate", SR_PRY AS "srPry", RNUM AS "seq" FROM( SELECT SR_NO, SR_TTL, SYS_NM, USER_NM, USER_DP_NM, USER_OGDP, SR_DEV_DP, STTS_NM, SR_REG_DATE, SR_DDLN_DATE, SR_PRY, ROWNUM AS RNUM FROM( SELECT s.SR_NO, s.SR_TTL, SYS_NM, u.USER_NM, u.USER_DP_NM, u.USER_OGDP, s.SR_DEV_DP, st.STTS_NM, s.SR_REG_DATE, s.SR_DDLN_DATE, s.SR_PRY
FROM SR s ,USERS u, SR_STTS st, SYSTEM sys
, HR hr
WHERE s.SR_CUST_NO = u.USER_NO
AND s.STTS_NO = st.STTS_NO AND s.SYS_NO = sys.SYS_NO AND s.SR_DEL_YN = 'N'
AND st.stts_no = #{sttsNo}
AND s.sys_no = #{sysNo}
AND s.sr_dev_dp LIKE #{srDevDp}
AND u.user_ogdp = #{userOgdp}
AND s.SR_REG_DATE BETWEEN TO_DATE(#{srRegStartDate},'YY-MM-DD') AND TO_DATE(#{srRegEndDate},'YY-MM-DD')+0.9
AND s.SR_TTL like #{srTtl}
AND s.SR_CUST_NO = #{userNo}
AND sys.MANAGER_NO = #{userNo}
AND hr.sr_no = s.sr_no AND hr.user_no = #{userNo}
ORDER BY DECODE(st.STTS_NO, 1, 2, 4, 5, 3, 7, 9, 8, 6) ASC, s.SR_REG_DATE DESC )
WHERE ROWNUM <=#{end}
)
WHERE RNUM >=#{start}
